/**
 * Plugin Description
 * 
 * @author Shoaib Shakeel
 * @version 1.5.0
 * 
 * @param {object} $ jQuery
 * @param {object} document
 * @param {object} window
 * @param {undefined} undefined 
 */
;(function($, document, window, undefined) {
    "use strict";
    var PluginName = function(elem, options) {
        var plugin = this,
                globals = {};
        
        plugin.defaults = {
            constant1: "value",
            constant2: "value",
        };
        
        plugin.settings = {
            
        };
        
        plugin.publicMethod = function() {
            
        };
        
        var init = function() {
            plugin.settings = $.extend({}, plugin.defaults, options);
            
            globals.$element = $(elem);
            privateMethod();
        };
        
        var privateMethod = function() {
            plugin.publicMethod();
        };
        
        init();
    };
    
    $.fn.pluginName = function(option) {
		var args = Array.prototype.slice.call(arguments, 1);
		var methodReturn;

		var $set = this.each(function () {
            var $this   = $(this);
            var data    = $this.data('pluginName');
            var options = typeof option === 'object' && option;
            
            if(!data) $this.data('pluginName', (data = new PluginName(this, options)));
            if(typeof option === 'string') {
                if(typeof data[option] === 'function') {
                    methodReturn = data[option].apply(data, args);
                } else {
                    methodReturn = data[option];
                }
            }
        });
        
        return (methodReturn === undefined) ? $set : methodReturn;
    };
})(jQuery, document, window);
